import * as React from 'react';
import { useState } from 'react';
import { Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

import ImportantScreen from './components/ImportantScreen';
import UnimportantScreen from './components/UnimportantScreen';
import SettingsScreen from './components/SettingsScreen';

const Tab = createBottomTabNavigator();

function displayNotification() {


}

export default function App() {
  const [importantNotifs, setImportantNotifs] = useState([{
    id: 0,
    AppName: "Snapchat",
    NotificationText: "Adam Inskip",
    TimeReceived: "12:30pm"
  },
  {
    id: 1,
    AppName: "Facebook",
    NotificationText: "Montel Yu",
    TimeReceived: "12:45pm"
  },
  {
    id: 2,
    AppName: "Instagram",
    NotificationText: "Alejandro Ramirez",
    TimeReceived: "6:20pm"
  }]);


  const addNotifs = event => {
    event.preventDefault();
      setImportantNotifs([
      ...notifs,
      {
        id: notifs.length,
        AppName: "Snapchat",
        NotificationText: "Adam Inskip",
        TimeReceived: "11:30a"
      }
    ])
  }

  return (
    <NavigationContainer>
      <Tab.Navigator>
        <Tab.Screen name="Important" children={()=><ImportantScreen notifs={importantNotifs} addNotifs={addNotifs}/>}/>
        <Tab.Screen name="Unimportant" children={()=><UnimportantScreen />}/>
        <Tab.Screen name="Settings" children={()=><SettingsScreen/>}/>
      </Tab.Navigator>
    </NavigationContainer>
  );
}



