import React, { useState } from 'react';
import { Text, View, FlatList, StyleSheet } from 'react-native';
import Notification from './Notification';

const styles = StyleSheet.create({
  item: {
    backgroundColor: '#f9c2ff',
    padding: 20,
    marginVertical: 25,
    marginHorizontal: 50,
  },
  title: {
    fontSize: 32,
  },
});

export default function ImportantScreen(props) {
    // const [notifs, setNotifs] = useState([{
    //   id: 0,
    //   AppName: "Snapchat",
    //   NotificationText: "Adam Inskip",
    //   TimeReceived: "12:30pm"
    // },
    // {
    //   id: 1,
    //   AppName: "Facebook",
    //   NotificationText: "Montel Yu",
    //   TimeReceived: "12:45pm"
    // },
    // {
    //   id: 2,
    //   AppName: "Instagram",
    //   NotificationText: "Alejandro Ramirez",
    //   TimeReceived: "6:20pm"
    // }]);

    const addNotifs = event => {
      event.preventDefault();
      setNotifs([
        ...notifs,
        {
          id: notifs.length,
          AppName: "Snapchat",
          NotificationText: "Adam Inskip",
          TimeReceived: "11:30a"
        }
      ])
    }



    return (
      <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
        <Text>Important Notifications!</Text>
        <FlatList data={props.notifs} renderItem={({item}) => 
            <View style={styles.item}>
              <Text>{item.AppName}</Text>
              <Text>{item.NotificationText}</Text>
              <Text>{item.TimeReceived}</Text>
            </View>
            }/>
        {/* <Notification AppName="Snapchat" NotificationText="Adam Inskip" TimeReceived="12:30pm" /> */}
      </View>
    );
}
